import axios from 'axios';

export const GET_DOGS = 'GET_DOGS';
export const GET_DOGS_BY_NAME = 'GET_DOGS_BY_NAME';
export const GET_TEMPERAMENTS = 'GET_TEMPERAMENTS';
export const FILTER_BY_TEMPERAMENT = 'FILTER_BY_TEMPERAMENT';
export const FILTER_BY_BREED = 'FILTER_BY_BREED';
export const SORT_BY_NAME = 'SORT_BY_NAME';
export const SORT_BY_WEIGHT = 'SORT_BY_WEIGHT';
export const GET_DETAILS = 'GET_DETAILS';
export const POST_DOGS = 'POST_DOGS';


export function getDogs(name) {
    return async function (dispatch) {
        try {
            if (name) {
                const dogsName = await axios.get('http://localhost:3001/dogs?name=' + name)
                return dispatch ({ 
                    type: GET_DOGS_BY_NAME, 
                    payload: dogsName.data })

                /* .then(res => dispatch({ type: GET_DOGS, payload: res.data }))
                .catch(err => console.log(err)) */
            }
            const dogs = await axios.get('http://localhost:3001/dogs');
            return dispatch({
                type: GET_DOGS,
                payload: dogs.data,
            })
        } catch (error) {
            console.log(error)
            };
        }
    }
export function getTemperaments(){
    return async function (dispatch){
        try {
            const temperaments = await axios.get('http://localhost:3001/temperaments');
            return dispatch({
                type: GET_TEMPERAMENTS,
                payload: temperaments.data
            })
        } catch (error) {
            console.log(error)
        }
    }
}

export function sortByName(payload){
