import React from "react";
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getDetail } from '../redux/actions/index';
import { useEffect } from "react";
import '../styles/Detail.css';

export default function Detail(props){
    console.log(props)
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(getDetail(props.match.params.id));
    },[dispatch])

    const myDog = useSelector((state) => state.detail)

    return (
        <div className="detail-container">
            {
                myDog.length?
                <div className='detail-card'>
                    <div top-row background-top-row>
                    <h1>Soy {myDog[0].name}</h1>
                    </div>
                    <img src={myDog[0].image} alt={myDog[0].name} ></img>
                   <h2>{myDog[0].createdInDb === true ? myDog[0].temperaments.map(t => t.name) :  myDog[0].temperaments.join(', ')}</h2>
                    <h3>Weight: {myDog[0].weightMin} - {myDog[0].weightMax}</h3>
                    <h3>Height: {myDog[0].heightMin} - {myDog[0].heightMax}</h3>
                    <h3>Age:{myDog[0].lifespan}</h3>
                </div>
                : <p>Loading...</p>
            }
            <Link to= '/home'>
                <button>Volver</button>
            </Link>
        </div>
    )
}
