import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import {  getTemperaments, postDog } from "../redux/actions";

const validate = (input)=>{
    let errors ={};
    if(!input.name) errors.name = 'Name is required'
    if(!input.name.length < 3 || input.name.length > 20) errors.name = 'Name must contain between 3 and 20 characters'
    if(!/^[a-zA-Z ]*$/.test(input.name)) errors.name = 'Invalid name, must only contain letters'
}
/* function validate(input) {
    let errors = {};
    if(!input.name) {
        errors.name = 'Se requiere un nombre';
    } else if (!input.life_span) {
        errors.life_span = 'Se requiere la duraciÃ³n de vida';
    } else if (!input.image) {
        errors.image = 'Se requiere un url de la imagen';
    }
    return errors;
}; */

export default function Form(){
    const dispatch = useDispatch();
    const history = useHistory();
    const temperaments = useSelector((state) => state.temperaments)
    const [errors, setErrors] = useState({});
    const [input, setInput] = useState({
        name:'',
        weightMin:0,
        weightMax:0,
        heightMin:0,
        heightMax:0,
        life_span:'',
        image:'',
        temperaments:[]
    })

    useEffect(()=>{
        dispatch(getTemperaments());
    },[dispatch]);

    function handleSelectTemperaments(e){
        setInput({
            ...input,
            temperaments:input.temperaments.includes(e.target.value) ? input.temperaments : [...input.temperaments, e.target.value]
        })
    }

    function handleChange(e){
        e.preventDefault();
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
    }

    /* setErrors(validate({
        ...input,
        [e.target.name]: e.target.value
    }));
    console.log(input) */

    function handleSubmit(e){
        e.preventDefault()
        dispatch(postDog(input))
        alert('Dog Created succesfully')
        history.push('/home')
    }

    function handleDelete(e){
        e.preventDefault()
        setInput({
            ...input,
            temperaments: input.temperaments.filter(t=>t !== e.target.id)
        })
    }

    return (
        <div>
            <Link to= '/home'><button>Back </button></Link>
            <h1>Create Dog</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                    type= 'text'
                    placeholder='name...'
                    value= {input.name}
                    name= 'name'
                    onChange={handleChange}
                    />
                    {errors.name && (
                        <p className="error">{errors.name}</p>
                    )}
                </div>
                <div>
                    <label>Height Min:</label>
                    <input
                    type= 'number'
                    placeholder="height min ..."
                    value= {input.heightMin}
                    name= 'heightMin'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Height Max:</label>
                    <input
                    type= 'number'
                    placeholder="height max..."
                    value= {input.heightMax}
                    name= 'heightMax'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Weight Min:</label>
                    <input
                    type= 'number'
                    placeholder="weight min..."
                    value= {input.weightMin}
                    name= 'weightMin'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Weight Max:</label>
                    <input
                    type= 'number'
                    placeholder="weight max..."
                    value= {input.weightMax}
                    name= 'weightMax'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Life Span:</label>
                    <input
                    type= 'number'
                    placeholder="life span..."
                    value= {input.life_span}
                    name= 'life_span'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Image:</label>
                    <input
                    type= 'text'
                    placeholder="url..."
                    value= {input.image}
                    name= 'image'
                    onChange={handleChange}
                    />
                </div>

                <select onChange={handleSelectTemperaments} >
                    {temperaments?.map((t)=>(<option value={t.name}>{t.name}</option>)
                    )}
                </select>
                <ul>
                {input.temperaments.map(t=>(
                        <li key={t} >
                            temperament: {t}
                            <button id={t} type='button' onClick={handleDelete} >deleteðŸ¦®</button>
                        </li>
                    ))}
                </ul>
                <button type='submit'>Submit âœ…</button>
            </form>
        </div>
    )

}
