import React from "react";
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getDetail } from '../redux/actions/index';
import { useEffect } from "react";

export default function Detail(props){
    console.log(props)
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(getDetail(props.match.params.id));
    },[dispatch])

    const myDog = useSelector((state) => state.detail)

    return (
        <div>
            {
                myDog.length?
                <div>
                    <h1>Soy {myDog[0].name}</h1>
                    <img src={myDog[0].image} alt={myDog[0].name} width='500px' height='700px'></img>
                   {/*  <h2>{myDog[0].temperaments && typeof myDog[0].temperaments === 'object' ? myDog[0].temperaments.map(e => e.name) : myDog[0].temperaments}</h2> */}
                   <h2>{ typeof myDog[0].temperaments === 'object' ? myDog[0].temperaments.map(t => t.name) :  myDog[0].temperaments.join(', ')}</h2>
                    <h3>{myDog[0].weightMin}</h3>
                    <h3>{myDog[0].weightMax}</h3>
                    <h3>{myDog[0].heightMin}</h3>
                    <h3>{myDog[0].heightMax}</h3>
                    <h3>{myDog[0].lifespan}edad</h3>
                </div>
                : <p>Loading...</p>
            }
            <Link to= '/home'>
                <button>Volver</button>
            </Link>
        </div>
    )
}
