import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import {  getTemperaments, postDog } from "../redux/actions";

const validate = (input)=>{
    let errors ={};
    if(!input.name) errors.name = 'Name is required'
    if(input.name.length < 3 || input.name.length > 30) errors.name = 'Name must contain between 3 and 20 characters'
    if(!/^[a-zA-Z\s]+$/.test(input.name)) errors.name = 'Invalid name, must only contain letters'
    if(!input.temperaments) errors.temperaments = 'Chosse a temperament'
    if(input.weightMin < 1 || input.weightMin > 70  || input.weightMin > input.weightMax)errors.weightMin = 'The value must be a positive number less than 80 kg and less than Weight Max'
    if(input.weightMax < 1 || input.weightMax > 100 || input.weightMax < input.weightMin)errors.weightMax = 'The value must be a positive number less than 150 kg and higher than Weight Min'
    if(input.heightMin < 1 || input.heightMin > 100 || input.heightMin > input.heightMax)errors.heightMin = 'The value must be a positive number less than 80 kg and less than Height Max'
    if(input.heightMax < 1 || input.heightMax > 150 || input.heightMax < input.heightMin)errors.heightMax = 'The value must be a positive number less than 150 kg and higher than Height Min'
   /*  if(/^\d+$^\d+$/.test(input.weightMin)) errors.weightMin = 'The number must be Integer'
    if(/^\d+$^\d+$/.test(input.weightMax)) errors.weightMax = 'The number must be Integer'
    if(/^\d+$^\d+$/.test(input.heightMin)) errors.heightMin = 'The number must be Integer'
    if(/^\d+$^\d+$/.test(input.heightMax)) errors.heightMax = 'The number must be Integer' */
    if(input.life_span < 1 || input.life_span > 80 ) errors.life_span = 'The value must be a positive number and less than 80 years'
    return errors
}



export default function Form(){
    const dispatch = useDispatch();
    const history = useHistory();
    const temperaments = useSelector((state) => state.temperaments)
    const [errors, setErrors] = useState({});
    const [input, setInput] = useState({
        name:'',
        weightMin:0,
        weightMax:0,
        heightMin:0,
        heightMax:0,
        life_span:'',
        image:'',
        temperaments:[]
    })

    useEffect(()=>{
        dispatch(getTemperaments());
    },[dispatch]);

    function handleSelectTemperaments(e){
        setInput({
            ...input,
            temperaments:input.temperaments.includes(e.target.value) ? input.temperaments : [...input.temperaments, e.target.value]
        })
    }

    function handleChange(e){
        e.preventDefault();
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }));
    }




    function handleSubmit(e){
        e.preventDefault()
        dispatch(postDog(input))
        alert('Dog Created succesfully')
        history.push('/home')
    }

    function handleDelete(e){
        e.preventDefault()
        setInput({
            ...input,
            temperaments: input.temperaments.filter(t=>t !== e.target.id)
        })
    }

    return (
        <div>
            <Link to= '/home'><button>Back </button></Link>
            <h1>Create Dog</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                    type= 'text'
                    placeholder='name...'
                    value= {input.name}
                    name= 'name'
                    onChange={handleChange}
                    />
                    {errors.name && (
                        <p className="error">{errors.name}</p>
                    )}
                </div>
                <div>
                    <label>Height Min:</label>
                    <input
                    type= 'number'
                    placeholder="height min ..."
                    value= {input.heightMin}
                    name= 'heightMin'
                    onChange={(e)=>handleChange(e)}
                    />
                    {errors.heightMin && (
                        <p className="error">{errors.heightMin}</p>
                    )}
                </div>
                <div>
                    <label>Height Max:</label>
                    <input
                    type= 'number'
                    placeholder="height max..."
                    value= {input.heightMax}
                    name= 'heightMax'
                    onChange={handleChange}
                    />
                    {errors.heightMax && (
                        <p className="error">{errors.heightMax}</p>
                    )}
                </div>
                <div>
                    <label>Weight Min:</label>
                    <input
                    type= 'number'
                    placeholder="weight min..."
                    value= {input.weightMin}
                    name= 'weightMin'
                    onChange={handleChange}
                    />
                    {errors.weightMin && (
                        <p className="error">{errors.weightMin}</p>
                    )}
                </div>
                <div>
                    <label>Weight Max:</label>
                    <input
                    type= 'number'
                    placeholder="weight max..."
                    value= {input.weightMax}
                    name= 'weightMax'
                    onChange={handleChange}
                    />
                    {errors.weightMax && (
                        <p className="error">{errors.weightMax}</p>
                    )}
                </div>
                <div>
                    <label>Life Span:</label>
                    <input
                    type= 'number'
                    placeholder="life span..."
                    value= {input.life_span}
                    name= 'life_span'
                    onChange={handleChange}
                    />
                    {errors.life_span && (
                        <p className="error">{errors.life_span}</p>
                    )}
                </div>
                <div>
                    <label>Image:</label>
                    <input
                    type= 'text'
                    placeholder="url..."
                    value= {input.image}
                    name= 'image'
                    onChange={handleChange}
                    />
                     {errors.image && (
                        <p className="error">{errors.image}</p>
                    )}
                </div>

                <select onChange={handleSelectTemperaments} >
                    {temperaments?.map((t)=>(<option value={t.name}>{t.name}</option>)
                    )}
                </select>
                {errors.temperaments && (
                        <p className="error">{errors.temperaments}</p>
                    )}
                <ul>
                {input.temperaments.map(t=>(
                        <li key={t} >
                            temperament: {t}
                            <button id={t} type='button' onClick={handleDelete} >delete🦮</button>
                        </li>
                    ))}
                </ul>
                <div>
                    {input.name.length < 2 || Object.keys(errors).length > 0 ? <button disabled>Submit</button> : <button type='submit'>Submit</button>}
                </div>

            </form>
        </div>
    )

}
