import { FILTER_BY_BREED, 
    FILTER_BY_TEMPERAMENT, 
    GET_DOGS, 
    GET_DOGS_BY_NAME, 
    GET_TEMPERAMENTS, 
    SORT_BY_NAME, 
    SORT_BY_WEIGHT,  } from "../actions";


const initialState = {
    dogs: [],
    allDogs: [],
    details: [],
    temperaments: []
}

function rootReducer (state = initialState, action){
    switch(action.type) {
        case GET_DOGS:
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload
            }
        case GET_DOGS_BY_NAME:
            return  {
                ...state,
                    dogs: action.payload,
            }
