                    dogs: sortedWeight,
            }
            case POST_DOGS:
                return {
                    ...state,
                }

            case GET_DETAIL:
                return {
                    ...state,
                        detail: action.payload,
                }
        default:
            return state;
    }
}

export default rootReducer;
