import axios from 'axios';

export const GET_DOGS = 'GET_DOGS';
export const GET_TEMPERAMENTS = 'GET_TEMPERAMENTS';
export const FILTER_BY_TEMPERAMENT = 'FILTER_BY_TEMPERAMENT';
export const FILTER_BY_ORIGIN = 'FILTER_BY_ORIGIN';
export const SORT_BY_NAME = 'SORT_BY_NAME';
export const SORT_BY_WEIGHT = 'SORT_BY_WEIGHT';
export const GET_DETAILS = 'GET_DETAILS';
export const SEARCH_FAILS = 'SEARCH_FAILS';

export function getDogs(name) {
    return async function (dispatch) {
        try {
            if (name) {
                return axios('http://localhost:3001/dogs?name=' + name)
                .then(res => dispatch({ type: GET_DOGS, payload: res.data }))
                .catch(err => dispatch({type: GET_DOGS, payload: err.data}))
            }
            const json = await axios('http://localhost:3001/dogs', {});
            return dispatch({
                type: GET_DOGS,
                payload: json.data,
            })
        } catch (error) {
            const fail = axios('http://localhost:3001/dogs?name=' + name)
            .then(res => res.data)
            return dispatch({
                type: SEARCH_FAIL,
                payload: fail
            });
        }
    }
}
