    GET_DOGS, 
    GET_DOGS_BY_NAME, 
    GET_TEMPERAMENTS, 
    POST_DOGS, 
    SORT_BY_NAME, 
    SORT_BY_WEIGHT,  } from "../actions";


const initialState = {
    dogs: [],
    allDogs: [],
    details: [],
    temperaments: []
}

function rootReducer (state = initialState, action){
    let pichus = []; // Array auxiliar para guardar los perros filtrados
    switch(action.type) {
        case GET_DOGS:
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload
            }
        case GET_DOGS_BY_NAME:
            return  {
                ...state,
                dogs: action.payload,
            }
        case GET_TEMPERAMENTS:
            return {
                ...state,
                temperaments: action.payload
            }
        case FILTER_BY_TEMPERAMENT:
        const allDogs = [...state.allDogs];
        console.log(allDogs.map(d=>d.temperament))
        const temperamentsFiltered = action.payload === 'all' ? allDogs : 
        allDogs.filter(d => d.temperament?.find(t => t.name === action.payload || t === action.payload))
        return {
            ...state,
            dogs: temperamentsFiltered
        }
        case FILTER_CREATED:
            if(action.payload === "all"){
                pichus = state.allDogs; // si el filtro es all, muestro todos los perros
