                pichus = state.allDogs.filter(dog => {
                    if(dog.createdInBd === false) return dog; // si el perro fue creado por la api lo muestro
                })
            } else {
                pichus = state.allDogs.filter(dog => {
                    if(dog.createdInBd === true) return dog; // si el perro fue creado por la api lo muestro
                })
            } 
            return{
                ...state,
                dogs: pichus
            }

        case SORT_BY_NAME:
            const sortedName = action.payload === 'asc' ?
                state.dogs.sort(function (a, b) {
                    if (a.name.toLowerCase() > b.name.toLowerCase()) {
                        return 1;
                    }
                    if (b.name.toLowerCase() > a.name.toLowerCase()) {
                        return -1;
                    }
                    return 0
                }) :
                state.dogs.sort(function (a, b) {
                    if (a.name.toLowerCase() > b.name.toLowerCase()) {
                        return -1;
                    }
                    if (b.name.toLowerCase() > a.name.toLowerCase()) {
                        return 1;
                    }
                    return 0;
                })
            return {
                ...state,
                    dogs: sortedName
            }
            case SORT_BY_WEIGHT:
                    const sortedWeight = action.payload === 'asc'?
                    state.dogs.sort(function (a,b) {
                        return parseInt(a.weight)-parseInt(b.weight);
                    }):
                     state.dogs.sort(function (a,b) {
                        return parseInt(b.weight)-parseInt(a.weight);
                    });
            return {
                ...state,
                    dogs: sortedWeight,
            }
            case POST_DOGS:
                return {
                    ...state,
                }

            case GET_DETAILS:
                return {
                    ...state,
                        detail: action.payload,
                }
        default:
            return state;
    }
}

export default rootReducer;
