import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { getDogs, getTemperaments, postDog } from "../redux/actions";

export default function Form(){
    const dispatch = useDispatch();
    const history = useHistory();
    const temperaments = useSelector((state) => state.temperaments)

    const [input, setInput] = useState({
        name:'',
        weightMin:0,
        weightMax:0,
        heightMin:0,
        heightMax:0,
        life_span:'',
        image:'',
        temperament:[]
    })

    useEffect(()=>{
        dispatch(getTemperaments());
    },[dispatch]);

    function handleSelectTemperaments(e){
        setInput({
            ...input,
            temperament:input.temperament.includes(e.target.value) ? input.temperament : [...input.temperament, e.target.value]
        })
    }

    function handleChange(e){
        e.preventDefault();
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
    }



    return (
        <div>
            <Link to= '/home'><button>Back</button></Link>
            <h1>Create Dog</h1>
            <form>
                <div>
                    <label>Name:</label>
                    <input
                    type= 'text'
                    value= {input.name}
                    name= 'name'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Heigth Min:</label>
                    <input
                    type= 'number'
                    value= {input.heightMin}
                    name= 'heightMin'
                    onChange={handleChange}

                    />
                </div>
                <div>
                    <label>Heigth Max:</label>
                    <input
                    type= 'number'
                    value= {input.heightMax}
                    name= 'heightMax'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Weigth Min:</label>
                    <input
                    type= 'number'
                    value= {input.weightMin}
                    name= 'weightMin'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Weigth Max:</label>
                    <input
                    type= 'number'
                    value= {input.weightMax}
                    name= 'weightMax'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Life Span:</label>
                    <input
                    type= 'number'
                    value= {input.life_span}
                    name= 'life_span'
                    onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Image:</label>
                    <input
                    type= 'text'
                    value= {input.image}
                    name= 'image'
                    onChange={handleChange}
                    />
                </div>
                <select onChange={handleSelectTemperaments}>
                    {temperaments?.map((t)=>(<option value={t.name}>{t.name}</option>)
                    )}
                </select>
                <button type='submit'>Submit</button>
            </form>
        </div>
    )

}
